import React, { Component } from 'react';
import {Form,Button} from "react-bootstrap"
import fire from '../config/Fire';
class Login extends Component {
  constructor(props) {
    super(props);
    this.login = this.login.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.signup = this.signup.bind(this);
    this.state = {
      email: '',
      password: ''
    };
  }

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  login(e) {
    e.preventDefault();
    fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u)=>{
    }).catch((error) => {
        console.log(error);
      });
  }

  signup(e){
    e.preventDefault();
    fire.auth().createUserWithEmailAndPassword(this.state.email, this.state.password).then((u)=>{
    }).then((u)=>{console.log(u)})
    .catch((error) => {
        console.log(error);
      })
  }
  render() {
    return (
      <div >
        <center>

          <br/>
          <br/>
          <h3>Login/Signup</h3>
          <br/>
          <br/>

        <div className="col-md-6" >
        <Form>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control value={this.state.email} onChange={this.handleChange} type="email" placeholder="Enter email" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control value={this.state.password} onChange={this.handleChange} type="password" placeholder="Password" />
  </Form.Group>
  <Button type="submit" onClick={this.login} variant="primary">
    Login
  </Button>
  <Button onClick={this.signup} style={{marginLeft: '25px'}} variant="success" type="submit">
    Sign Up
  </Button>
</Form>
